<?php	if($_POST['action']=='plugin_status_change')	{		//$log->debug($_POST['plugin_name'].' '.$_POST['stauts']);		if($_POST['status']=='active')		{			$array=get_active_plugins();			if($array=='') $array=array();			$array=array_diff($array,array($_POST['plugin_name']));			update_option('active_plugins',serialize($array));		}else{			$array=get_active_plugins();			if($array=='') $array=array();			array_push($array,$_POST['plugin_name']);			update_option('active_plugins',serialize($array));		}	}?>				<div>			<div id="table_head"></div>			<table>				<thead>					<tr>						<th>plubin_name</th>						<th>status</th>						<th>change status</th>					</tr>				</thead>				<tbody>					<?php						$active_plugins=get_active_plugins();						if($active_plugins=='') $active_plugins=array();						foreach(get_all_plugins() as $p)						{							$status=in_array($p,$active_plugins)?'active':'deactive';							echo "<tr><form method='post' action='index.php#plugins'><td><input type='hidden' name='plugin_name' value='$p'/>$p</td>							<td><input type='hidden' name='status' value='$status'/>$status</td>							<td><button class='btn btn-large btn-block' type='submit' formmethod='post' name='action' value='plugin_status_change' >change</button></td></form></tr>";						}						//$log->debug($active_plugins);						//$log->debug(get_all_plugins());					?>				</tbody>			</table>			<div id="table_foot"></div>		</div>